// server/emailService.ts
import nodemailer from "nodemailer";
var transporter = nodemailer.createTransport({
  service: "gmail",
  auth: {
    user: process.env.EMAIL_USER,
    pass: process.env.EMAIL_PASS
  }
});
var sendContactEmail = async (data) => {
  const { name, email, subject, message } = data;
  try {
    const mailOptions = {
      from: `"Portfolio Contact Form" <${process.env.EMAIL_USER}>`,
      to: process.env.EMAIL_USER,
      // Send to your own email
      replyTo: email,
      // Set reply-to as the sender's email
      subject: `Portfolio Contact: ${subject}`,
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
          <h2 style="color: #8a2be2;">New message from your portfolio website</h2>
          <hr style="border: 1px solid #eee;">
          <p><strong>Name:</strong> ${name}</p>
          <p><strong>Email:</strong> ${email}</p>
          <p><strong>Subject:</strong> ${subject}</p>
          <div style="margin-top: 20px;">
            <h3 style="color: #444;">Message:</h3>
            <p style="background: #f5f5f5; padding: 15px; border-radius: 5px;">${message.replace(/\n/g, "<br>")}</p>
          </div>
          <hr style="border: 1px solid #eee; margin-top: 20px;">
          <p style="color: #777; font-size: 12px;">This email was sent from the contact form on your portfolio website.</p>
        </div>
      `
    };
    const info = await transporter.sendMail(mailOptions);
    console.log("Email sent successfully:", info.messageId);
    return true;
  } catch (error) {
    console.error("Error sending email:", error);
    return false;
  }
};

// server/routes.ts
async function registerRoutes(app2) {
  app2.post("/api/contact", async (req, res) => {
    const { name, email, subject, message } = req.body;
    if (!name || !email || !message) {
      return res.status(400).json({
        success: false,
        message: "Please provide all required fields"
      });
    }
    try {
      const emailSent = await sendContactEmail({
        name,
        email,
        subject: subject || "Message from Portfolio Contact Form",
        message
      });
      if (emailSent) {
        console.log("Contact form email sent successfully.");
        return res.status(200).json({
          success: true,
          message: "Your message has been sent. Thank you for reaching out!"
        });
      } else {
        console.error("Failed to send contact form email.");
        return res.status(500).json({
          success: false,
          message: "There was an error sending your message. Please try again later."
        });
      }
    } catch (error) {
      console.error("Error handling contact form submission:", error);
      return res.status(500).json({
        success: false,
        message: "There was an error processing your request. Please try again later."
      });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

